mapper.select:
SELECT B.iuser, B.pic, B.firebase_token AS firebaseToken
FROM t_dm_user A
INNER JOIN t_user B
ON A.iuser = B.iuser
WHERE A.idm = #{idm}
AND A.iuser != #{loginedIuser}

mapper.update:
UPDATE t_dm
SET last_msg = #{msg}
WHERE idm = #{idm}

mapper.select:
SELECT idm
FROM t_dm_user
WHERE iuser = #{loginedIuser}
OR iuser = #{otherPersonIuser}
GROUP BY idm
HAVING COUNT(idm) = 2

mapper.insert:

    SELECT IFNULL(MAX(seq), 0) + 1
    FROM t_dm_msg
    WHERE idm = #{idm}

INSERT INTO t_dm_msg
SET idm = #{idm}
, seq = #{seq}
, iuser = #{loginedIuser}
, msg = #{msg}

mapper.delete:
DELETE FROM t_dm_msg
WHERE idm = #{idm}
AND seq = #{seq}
AND iuser = #{iuser}
